import React from 'react';
import { ChevronRight } from 'lucide-react';
import { calculateTotalPrice } from '../../utils/priceCalculator';

const BottomSelectionBar = ({ selectedSkip, onClearSelection, onContinue }) => {
  if (!selectedSkip) return null;

  const totalPrice = calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat);

  return (
    <div style={styles.bottomBar}>
      <div style={styles.bottomBarContent}>
        <div style={styles.bottomBarInfo}>
          <h3 style={styles.bottomBarTitle}>
            Selected: {selectedSkip.size} Yard Skip
          </h3>
          <p style={styles.bottomBarSubtitle}>
            Â£{totalPrice} including VAT
          </p>
        </div>
        
        <div style={styles.bottomBarActions}>
          <button
            onClick={onClearSelection}
            style={styles.changeButton}
          >
            Change Selection
          </button>
          <button
            onClick={onContinue}
            style={styles.continueButton}
          >
            <span>Continue</span>
            <ChevronRight size={16} style={styles.continueIcon} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default BottomSelectionBar;
