import React from 'react';
import { Calendar, Truck, Weight, Check } from 'lucide-react';
import SkipStatusIndicator from './SkipStatusIndicator';
import PriceBreakdown from './PriceBreakdown';
import { calculateTotalPrice } from '../../utils/priceCalculator';

const SkipCard = ({ skip, isSelected, onSelect }) => {
  const totalPrice = calculateTotalPrice(skip.price_before_vat, skip.vat);

  return (
    <div
      className={`group relative bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 cursor-pointer transform hover:-translate-y-1 ${
        isSelected ? 'ring-4 ring-blue-500 ring-opacity-50 shadow-2xl' : ''
      }`}
      onClick={() => onSelect(skip)}
    >
      {/* Selected Badge */}
      {isSelected && (
        <div className="absolute -top-2 -right-2 bg-blue-500 text-white rounded-full w-8 h-8 flex items-center justify-center z-10">
          <Check className="w-4 h-4" />
        </div>
      )}

      {/* Skip Size Header */}
      <div className={`p-6 pb-4 ${isSelected ? 'bg-blue-50' : 'bg-gray-50'} rounded-t-2xl`}>
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-2xl font-bold text-gray-900">{skip.size}</h3>
          <span className="text-sm font-medium text-gray-500">YARD</span>
        </div>
        <div className="text-3xl font-bold text-blue-600">
          Â£{totalPrice}
          <span className="text-sm font-normal text-gray-500 ml-1">inc VAT</span>
        </div>
      </div>

      {/* Skip Details */}
      <div className="p-6 pt-4">
        <div className="space-y-3 mb-4">
          <div className="flex items-center text-sm text-gray-600">
            <Calendar className="w-4 h-4 mr-2 text-gray-400" />
            {skip.hire_period_days} day hire period
          </div>
          
          <SkipStatusIndicator
            icon={Truck}
            isPositive={skip.allowed_on_road}
            label={skip.allowed_on_road ? 'Road placement allowed' : 'Private land only'}
          />
          
          <SkipStatusIndicator
            icon={Weight}
            isPositive={skip.allows_heavy_waste}
            label={skip.allows_heavy_waste ? 'Heavy waste accepted' : 'Light waste only'}
          />
        </div>

        <PriceBreakdown skip={skip} />

        {/* Select Button */}
        <button
          className={`w-full mt-4 px-4 py-3 rounded-xl font-semibold transition-all duration-200 ${
            isSelected
              ? 'bg-blue-500 text-white shadow-lg'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200 group-hover:bg-blue-50 group-hover:text-blue-600'
          }`}
        >
          {isSelected ? 'Selected' : 'Select This Skip'}
        </button>
      </div>
    </div>
  );
};

export default SkipCard;
