import React from 'react';
import { ChevronRight } from 'lucide-react';
import { calculateTotalPrice } from '../../utils/priceCalculator';

const BottomSelectionBar = ({ selectedSkip, onClearSelection, onContinue }) => {
  if (!selectedSkip) return null;

  const totalPrice = calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat);

  return (
    <>
      <div className="fixed bottom-0 left-0 right-0 bg-white border-t shadow-lg p-4 md:relative md:shadow-none md:border-0 md:bg-transparent md:p-0">
        <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between space-y-4 md:space-y-0">
          <div className="text-center md:text-left">
            <h3 className="font-semibold text-gray-900">
              Selected: {selectedSkip.size} Yard Skip
            </h3>
            <p className="text-sm text-gray-600">
              Â£{totalPrice} including VAT
            </p>
          </div>
          
          <div className="flex space-x-4">
            <button
              onClick={onClearSelection}
              className="px-6 py-3 text-gray-600 hover:text-gray-800 font-medium transition-colors"
            >
              Change Selection
            </button>
            <button
              onClick={onContinue}
              className="bg-blue-600 hover:bg-blue-700 text-white px-8 py-3 rounded-xl font-semibold flex items-center space-x-2 transition-colors shadow-lg hover:shadow-xl"
            >
              <span>Continue</span>
              <ChevronRight className="w-4 h-4" />
            </button>
          </div>
        </div>
      </div>
      {/* Mobile spacing for fixed bottom bar */}
      <div className="h-24 md:h-0"></div>
    </>
  );
};

export default BottomSelectionBar;
