import React from 'react';

import { calculateTotalPrice, calculateVatAmount } from '../../utils/priceCalculator';

const PriceBreakdown = ({ skip }) => {
  const vatAmount = calculateVatAmount(skip.price_before_vat, skip.vat);
  const totalPrice = calculateTotalPrice(skip.price_before_vat, skip.vat);

  return (
    <div className="border-t border-gray-200 pt-4 mt-4">
      <div className="flex justify-between text-sm text-gray-600 mb-1">
        <span>Base price</span>
        <span>£{skip.price_before_vat}</span>
      </div>
      <div className="flex justify-between text-sm text-gray-600 mb-1">
        <span>VAT ({skip.vat}%)</span>
        <span>£{vatAmount}</span>
      </div>
      <div className="flex justify-between font-semibold text-gray-900 border-t border-gray-200 pt-2 mt-2">
        <span>Total</span>
        <span>£{totalPrice}</span>
      </div>
    </div>
  );
};


export default PriceBreakdown;
