import React, { useState } from 'react';
import { useSkipData } from './hooks/useSkipData';
import LoadingSpinner from './components/UI/LoadingSpinner';
import ErrorMessage from './components/UI/ErrorMessage';
import PageHeader from './components/PageHeader/PageHeader';
import SkipGrid from './components/SkipGrid/SkipGrid';
import BottomSelectionBar from './components/BottomSelectionBar/BottomSelectionBar';
import { calculateTotalPrice } from './utils/priceCalculator';

const SkipSelector = () => {
  const { skips, loading, error } = useSkipData();
  const [selectedSkip, setSelectedSkip] = useState(null);

  const handleSkipSelect = (skip) => {
    setSelectedSkip(skip);
  };

  const handleClearSelection = () => {
    setSelectedSkip(null);
  };

  const handleContinue = () => {
    if (selectedSkip) {
      const totalPrice = calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat);
      alert(`Selected ${selectedSkip.size} Yard Skip - £${totalPrice}`);
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage error={error} />;

  return (
    <div style={styles.container}>
      <PageHeader />
      
      <div style={styles.main}>
        <SkipGrid 
          skips={skips}
          selectedSkip={selectedSkip}
          onSkipSelect={handleSkipSelect}
        />
        
        <BottomSelectionBar
          selectedSkip={selectedSkip}
          onClearSelection={handleClearSelection}
          onContinue={handleContinue}
        />
      </div>
    </div>
  );
}
export default SkipSelector;
