import React, { useState } from 'react';
import { useSkipData } from './hooks/useSkipData';
import LoadingSpinner from './components/UI/LoadingSpinner';
import ErrorMessage from './components/UI/ErrorMessage';
import { calculateTotalPrice } from './utils/priceCalculator';
import { 
  Calendar, 
  Truck, 
  Weight, 
  Check, 
  ChevronRight, 
  MapPin,
  Clock,
  Shield,
  Star
} from 'lucide-react';

// Updated Dark Theme Skip Selector with Modern Design
const SkipSelector = () => {
  const { skips, loading, error } = useSkipData();
  const [selectedSkip, setSelectedSkip] = useState(null);
  const [hoveredSkip, setHoveredSkip] = useState(null);

  const styles = {
    container: {
      minHeight: '100vh',
      backgroundColor: '#0f172a',
      color: '#ffffff',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    },
    header: {
      background: 'linear-gradient(135deg, #1e293b 0%, #334155 100%)',
      borderBottom: '1px solid #334155',
      position: 'sticky',
      top: 0,
      zIndex: 100,
    },
    headerContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '2rem 1rem',
    },
    stepIndicator: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
      marginBottom: '2rem',
      fontSize: '0.875rem',
      color: '#94a3b8',
    },
    stepItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
    },
    stepActive: {
      color: '#3b82f6',
      fontWeight: '600',
    },
    stepNumber: {
      width: '24px',
      height: '24px',
      borderRadius: '50%',
      backgroundColor: '#3b82f6',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '0.75rem',
      fontWeight: 'bold',
    },
    headerTitle: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.5rem 0',
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
    },
    headerSubtitle: {
      color: '#94a3b8',
      fontSize: '1.125rem',
      margin: 0,
    },
    main: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '3rem 1rem',
    },
    skipGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(320px, 1fr))',
      gap: '2rem',
      marginBottom: '3rem',
    },
    skipCard: {
      backgroundColor: '#1e293b',
      borderRadius: '1.5rem',
      overflow: 'hidden',
      cursor: 'pointer',
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      border: '2px solid transparent',
      position: 'relative',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.3)',
    },
    skipCardHover: {
      transform: 'translateY(-8px) scale(1.02)',
      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.4)',
      borderColor: '#3b82f6',
    },
    skipCardSelected: {
      borderColor: '#3b82f6',
      boxShadow: '0 0 0 4px rgba(59, 130, 246, 0.3), 0 20px 40px rgba(0, 0, 0, 0.4)',
      transform: 'translateY(-4px)',
    },
    skipImageContainer: {
      position: 'relative',
      height: '200px',
      background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      overflow: 'hidden',
    },
    skipImagePattern: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      opacity: 0.1,
      backgroundImage: 'radial-gradient(circle at 20% 20%, white 2px, transparent 2px)',
      backgroundSize: '20px 20px',
    },
    skipImage: {
      width: '150px',
      height: '100px',
      backgroundColor: '#f59e0b',
      borderRadius: '8px',
      position: 'relative',
      boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
      transform: 'perspective(200px) rotateX(10deg)',
    },
    skipImageSide: {
      position: 'absolute',
      top: '10px',
      left: '-10px',
      width: '10px',
      height: '80px',
      backgroundColor: '#d97706',
      transformOrigin: 'right',
      transform: 'skewY(-45deg)',
    },
    skipImageBottom: {
      position: 'absolute',
      bottom: '-10px',
      left: '0',
      right: '0',
      height: '10px',
      backgroundColor: '#d97706',
      transformOrigin: 'top',
      transform: 'skewX(-45deg)',
    },
    sizeBadge: {
      position: 'absolute',
      top: '1rem',
      right: '1rem',
      backgroundColor: '#3b82f6',
      color: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '2rem',
      fontSize: '0.875rem',
      fontWeight: 'bold',
      boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)',
    },
    selectedBadge: {
      position: 'absolute',
      top: '1rem',
      left: '1rem',
      backgroundColor: '#10b981',
      color: 'white',
      padding: '0.5rem',
      borderRadius: '50%',
      boxShadow: '0 4px 12px rgba(16, 185, 129, 0.4)',
    },
    skipCardContent: {
      padding: '2rem',
    },
    skipTitle: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.5rem 0',
    },
    skipSubtitle: {
      color: '#94a3b8',
      fontSize: '0.875rem',
      margin: '0 0 1.5rem 0',
    },
    skipFeatures: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.75rem',
      marginBottom: '1.5rem',
    },
    featureItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      fontSize: '0.875rem',
    },
    featureIcon: {
      width: '16px',
      height: '16px',
      color: '#10b981',
    },
    featureIconWarning: {
      color: '#f59e0b',
    },
    featureText: {
      color: '#e2e8f0',
    },
    priceSection: {
      borderTop: '1px solid #334155',
      paddingTop: '1.5rem',
      marginTop: '1.5rem',
    },
    priceRow: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontSize: '0.875rem',
      color: '#94a3b8',
      marginBottom: '0.5rem',
    },
    totalPrice: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#ffffff',
      marginTop: '0.75rem',
      paddingTop: '0.75rem',
      borderTop: '1px solid #334155',
    },
    selectButton: {
      width: '100%',
      marginTop: '1.5rem',
      padding: '1rem',
      borderRadius: '1rem',
      fontWeight: '600',
      fontSize: '0.875rem',
      transition: 'all 0.3s ease',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.5rem',
    },
    selectButtonDefault: {
      backgroundColor: '#374151',
      color: '#d1d5db',
      border: '1px solid #4b5563',
    },
    selectButtonHover: {
      backgroundColor: '#4b5563',
      transform: 'translateY(-2px)',
      boxShadow: '0 8px 20px rgba(0, 0, 0, 0.3)',
    },
    selectButtonSelected: {
      backgroundColor: '#3b82f6',
      color: 'white',
      boxShadow: '0 8px 20px rgba(59, 130, 246, 0.4)',
    },
    bottomBar: {
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: '#1e293b',
      borderTop: '1px solid #334155',
      padding: '1.5rem',
      boxShadow: '0 -10px 30px rgba(0, 0, 0, 0.3)',
      zIndex: 50,
    },
    bottomBarContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      gap: '2rem',
    },
    bottomBarInfo: {
      flex: 1,
    },
    bottomBarTitle: {
      fontSize: '1.125rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.25rem 0',
    },
    bottomBarSubtitle: {
      color: '#94a3b8',
      fontSize: '0.875rem',
      margin: 0,
    },
    bottomBarActions: {
      display: 'flex',
      gap: '1rem',
      alignItems: 'center',
    },
    changeButton: {
      padding: '0.75rem 1.5rem',
      backgroundColor: 'transparent',
      color: '#94a3b8',
      border: '1px solid #4b5563',
      borderRadius: '0.75rem',
      cursor: 'pointer',
      transition: 'all 0.2s ease',
      fontSize: '0.875rem',
      fontWeight: '500',
    },
    continueButton: {
      backgroundColor: '#3b82f6',
      color: 'white',
      padding: '1rem 2rem',
      borderRadius: '0.75rem',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.2s ease',
      border: 'none',
      cursor: 'pointer',
      boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)',
      fontSize: '0.875rem',
    },
  };

  const handleSkipSelect = (skip) => {
    setSelectedSkip(skip);
  };

  const handleClearSelection = () => {
    setSelectedSkip(null);
  };

  const handleContinue = () => {
    if (selectedSkip) {
      const totalPrice = calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat);
      alert(`Selected ${selectedSkip.size} Yard Skip - Â£${totalPrice}`);
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage error={error} />;

  return (
    <div style={styles.container}>
      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.stepIndicator}>
            <div style={{...styles.stepItem, ...styles.stepActive}}>
              <div style={styles.stepNumber}>3</div>
              <span>Select Skip</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <MapPin size={16} />
              <span>Permit Check</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <Calendar size={16} />
              <span>Choose Date</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <Shield size={16} />
              <span>Payment</span>
            </div>
          </div>
          
          <h1 style={styles.headerTitle}>Choose Your Skip Size</h1>
          <p style={styles.headerSubtitle}>
            Select the skip size that best suits your needs
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div style={styles.main}>
        <div style={styles.skipGrid}>
          {skips.map((skip) => {
            const isSelected = selectedSkip?.id === skip.id;
            const isHovered = hoveredSkip === skip.id;
            const totalPrice = calculateTotalPrice(skip.price_before_vat, skip.vat);
            const vatAmount = (skip.price_before_vat * skip.vat / 100).toFixed(0);

            const cardStyle = {
              ...styles.skipCard,
              ...(isSelected ? styles.skipCardSelected : {}),
              ...(isHovered && !isSelected ? styles.skipCardHover : {}),
            };

            const buttonStyle = {
              ...styles.selectButton,
              ...(isSelected ? styles.selectButtonSelected : styles.selectButtonDefault),
              ...(isHovered && !isSelected ? styles.selectButtonHover : {}),
            };

            return (
              <div
                key={skip.id}
                style={cardStyle}
                onClick={() => handleSkipSelect(skip)}
                onMouseEnter={() => setHoveredSkip(skip.id)}
                onMouseLeave={() => setHoveredSkip(null)}
              >
                {/* Skip Image */}
                <div style={styles.skipImageContainer}>
                  <div style={styles.skipImagePattern}></div>
                  <div style={styles.skipImage}>
                    <div style={styles.skipImageSide}></div>
                    <div style={styles.skipImageBottom}></div>
                  </div>
                  
                  <div style={styles.sizeBadge}>
                    {skip.size} Yards
                  </div>
                  
                  {isSelected && (
                    <div style={styles.selectedBadge}>
                      <Check size={16} />
                    </div>
                  )}
                </div>

                {/* Card Content */}
                <div style={styles.skipCardContent}>
                  <h3 style={styles.skipTitle}>{skip.size} Yard Skip</h3>
                  <p style={styles.skipSubtitle}>{skip.hire_period_days} day hire period</p>

                  {/* Features */}
                  <div style={styles.skipFeatures}>
                    <div style={styles.featureItem}>
                      <Clock style={styles.featureIcon} size={16} />
                      <span style={styles.featureText}>
                        {skip.hire_period_days} days included
                      </span>
                    </div>
                    
                    <div style={styles.featureItem}>
                      <Truck 
                        style={skip.allowed_on_road ? styles.featureIcon : styles.featureIconWarning} 
                        size={16} 
                      />
                      <span style={styles.featureText}>
                        {skip.allowed_on_road ? 'Road placement allowed' : 'Private land only'}
                      </span>
                    </div>
                    
                    <div style={styles.featureItem}>
                      <Weight 
                        style={skip.allows_heavy_waste ? styles.featureIcon : styles.featureIconWarning} 
                        size={16} 
                      />
                      <span style={styles.featureText}>
                        {skip.allows_heavy_waste ? 'Heavy waste accepted' : 'Light waste only'}
                      </span>
                    </div>
                  </div>

                  {/* Pricing */}
                  <div style={styles.priceSection}>
                    <div style={styles.priceRow}>
                      <span>Base price</span>
                      <span>Â£{skip.price_before_vat}</span>
                    </div>
                    <div style={styles.priceRow}>
                      <span>VAT ({skip.vat}%)</span>
                      <span>Â£{vatAmount}</span>
                    </div>
                    <div style={styles.totalPrice}>
                      <span>Total</span>
                      <span>Â£{totalPrice}</span>
                    </div>
                  </div>

                  {/* Select Button */}
                  <button style={buttonStyle}>
                    {isSelected ? (
                      <>
                        <Check size={16} />
                        Selected
                      </>
                    ) : (
                      <>
                        <Star size={16} />
                        Select This Skip
                      </>
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Bottom Selection Bar */}
      {selectedSkip && (
        <div style={styles.bottomBar}>
          <div style={styles.bottomBarContent}>
            <div style={styles.bottomBarInfo}>
              <h3 style={styles.bottomBarTitle}>
                Selected: {selectedSkip.size} Yard Skip
              </h3>
              <p style={styles.bottomBarSubtitle}>
                Â£{calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat)} including VAT
              </p>
            </div>
            
            <div style={styles.bottomBarActions}>
              <button
                onClick={handleClearSelection}
                style={styles.changeButton}
              >
                Change Selection
              </button>
              <button
                onClick={handleContinue}
                style={styles.continueButton}
              >
                <span>Continue</span>
                <ChevronRight size={16} />
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SkipSelector;
