import React, { useState, useEffect } from 'react';
import { 
  Calendar, 
  Truck, 
  Weight, 
  Check, 
  ChevronRight, 
  MapPin,
  Clock,
  Shield,
  Star,
  Sparkles,
  Zap
} from 'lucide-react';

// Mock data and hooks for demonstration
const mockSkips = [
  {
    id: 1,
    size: 4,
    price_before_vat: 120,
    vat: 20,
    hire_period_days: 7,
    allowed_on_road: true,
    allows_heavy_waste: false
  },
  {
    id: 2,
    size: 6,
    price_before_vat: 150,
    vat: 20,
    hire_period_days: 7,
    allowed_on_road: true,
    allows_heavy_waste: true
  },
  {
    id: 3,
    size: 8,
    price_before_vat: 180,
    vat: 20,
    hire_period_days: 7,
    allowed_on_road: false,
    allows_heavy_waste: true
  }
];

const useSkipData = () => ({
  skips: mockSkips,
  loading: false,
  error: null
});

const calculateTotalPrice = (priceBeforeVat, vat) => {
  return Math.round(priceBeforeVat * (1 + vat / 100));
};

const SkipSelector = () => {
  const { skips, loading, error } = useSkipData();
  const [selectedSkip, setSelectedSkip] = useState(null);
  const [hoveredSkip, setHoveredSkip] = useState(null);
  const [isVisible, setIsVisible] = useState(false);
  const [particleAnimation, setParticleAnimation] = useState(false);

  useEffect(() => {
    setIsVisible(true);
    // Trigger particle animation on mount
    const timer = setTimeout(() => setParticleAnimation(true), 500);
    return () => clearTimeout(timer);
  }, []);

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f172a 0%, #1e1b4b 50%, #0f172a 100%)',
      color: '#ffffff',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
      position: 'relative',
      overflow: 'hidden',
    },
    backgroundPattern: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundImage: 'radial-gradient(circle at 25% 25%, rgba(59, 130, 246, 0.1) 0%, transparent 50%), radial-gradient(circle at 75% 75%, rgba(139, 92, 246, 0.1) 0%, transparent 50%)',
      animation: 'float 20s ease-in-out infinite alternate',
    },
    floatingElements: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      pointerEvents: 'none',
      zIndex: 1,
    },
    particle: {
      position: 'absolute',
      width: '4px',
      height: '4px',
      backgroundColor: '#3b82f6',
      borderRadius: '50%',
      animation: 'particle-float 15s linear infinite',
      opacity: 0.6,
    },
    header: {
      background: 'linear-gradient(135deg, rgba(30, 41, 59, 0.9) 0%, rgba(51, 65, 85, 0.9) 100%)',
      backdropFilter: 'blur(20px)',
      borderBottom: '1px solid rgba(59, 130, 246, 0.2)',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      transform: isVisible ? 'translateY(0)' : 'translateY(-100%)',
      transition: 'transform 0.8s cubic-bezier(0.4, 0, 0.2, 1)',
    },
    headerContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '2rem 1rem',
    },
    stepIndicator: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
      marginBottom: '2rem',
      fontSize: '0.875rem',
      color: '#94a3b8',
    },
    stepItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'all 0.3s ease',
    },
    stepActive: {
      color: '#3b82f6',
      fontWeight: '600',
      transform: 'scale(1.05)',
    },
    stepNumber: {
      width: '28px',
      height: '28px',
      borderRadius: '50%',
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
      color: 'white',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '0.75rem',
      fontWeight: 'bold',
      boxShadow: '0 4px 12px rgba(59, 130, 246, 0.4)',
      animation: 'pulse-glow 2s ease-in-out infinite',
    },
    headerTitle: {
      fontSize: '3rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.5rem 0',
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6, #ec4899)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
      animation: 'gradient-shift 3s ease-in-out infinite alternate',
      transform: isVisible ? 'translateY(0) scale(1)' : 'translateY(20px) scale(0.9)',
      transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.2s',
    },
    headerSubtitle: {
      color: '#94a3b8',
      fontSize: '1.25rem',
      margin: 0,
      transform: isVisible ? 'translateY(0) opacity(1)' : 'translateY(20px) opacity(0)',
      transition: 'all 0.8s cubic-bezier(0.4, 0, 0.2, 1) 0.4s',
    },
    main: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '3rem 1rem',
      position: 'relative',
      zIndex: 2,
    },
    skipGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
      gap: '2.5rem',
      marginBottom: '3rem',
    },
    skipCard: {
      backgroundColor: 'rgba(30, 41, 59, 0.6)',
      backdropFilter: 'blur(20px)',
      borderRadius: '2rem',
      overflow: 'hidden',
      cursor: 'pointer',
      transition: 'all 0.5s cubic-bezier(0.4, 0, 0.2, 1)',
      border: '2px solid rgba(59, 130, 246, 0.2)',
      position: 'relative',
      boxShadow: '0 8px 32px rgba(0, 0, 0, 0.3)',
      transform: isVisible ? 'translateY(0) scale(1)' : 'translateY(40px) scale(0.9)',
      opacity: isVisible ? 1 : 0,
    },
    skipCardHover: {
      transform: 'translateY(-12px) scale(1.03)',
      boxShadow: '0 32px 64px rgba(59, 130, 246, 0.2), 0 0 40px rgba(59, 130, 246, 0.1)',
      borderColor: 'rgba(59, 130, 246, 0.5)',
    },
    skipCardSelected: {
      borderColor: '#3b82f6',
      boxShadow: '0 0 0 4px rgba(59, 130, 246, 0.3), 0 32px 64px rgba(59, 130, 246, 0.3)',
      transform: 'translateY(-8px) scale(1.02)',
      background: 'linear-gradient(135deg, rgba(30, 41, 59, 0.8) 0%, rgba(59, 130, 246, 0.1) 100%)',
    },
    skipImageContainer: {
      position: 'relative',
      height: '220px',
      background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 50%, #d97706 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      overflow: 'hidden',
    },
    skipImagePattern: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      opacity: 0.15,
      backgroundImage: 'radial-gradient(circle at 30% 30%, white 3px, transparent 3px), radial-gradient(circle at 70% 70%, white 2px, transparent 2px)',
      backgroundSize: '30px 30px, 20px 20px',
      animation: 'pattern-move 10s linear infinite',
    },
    skipImage: {
      width: '160px',
      height: '110px',
      backgroundColor: '#f59e0b',
      borderRadius: '12px',
      position: 'relative',
      boxShadow: '0 20px 40px rgba(0, 0, 0, 0.4)',
      transform: 'perspective(300px) rotateX(15deg) rotateY(5deg)',
      transition: 'transform 0.5s ease',
    },
    skipImageHover: {
      transform: 'perspective(300px) rotateX(10deg) rotateY(-5deg) scale(1.05)',
    },
    skipImageSide: {
      position: 'absolute',
      top: '12px',
      left: '-12px',
      width: '12px',
      height: '86px',
      backgroundColor: '#d97706',
      transformOrigin: 'right',
      transform: 'skewY(-45deg)',
    },
    skipImageBottom: {
      position: 'absolute',
      bottom: '-12px',
      left: '0',
      right: '0',
      height: '12px',
      backgroundColor: '#d97706',
      transformOrigin: 'top',
      transform: 'skewX(-45deg)',
    },
    sizeBadge: {
      position: 'absolute',
      top: '1.5rem',
      right: '1.5rem',
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
      color: 'white',
      padding: '0.75rem 1.25rem',
      borderRadius: '2rem',
      fontSize: '0.875rem',
      fontWeight: 'bold',
      boxShadow: '0 8px 24px rgba(59, 130, 246, 0.4)',
      animation: 'badge-glow 2s ease-in-out infinite alternate',
    },
    selectedBadge: {
      position: 'absolute',
      top: '1.5rem',
      left: '1.5rem',
      background: 'linear-gradient(135deg, #10b981, #059669)',
      color: 'white',
      padding: '0.75rem',
      borderRadius: '50%',
      boxShadow: '0 8px 24px rgba(16, 185, 129, 0.4)',
      animation: 'success-pulse 1s ease-in-out',
    },
    skipCardContent: {
      padding: '2.5rem',
    },
    skipTitle: {
      fontSize: '1.75rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.5rem 0',
      background: 'linear-gradient(135deg, #ffffff, #e2e8f0)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
    },
    skipSubtitle: {
      color: '#94a3b8',
      fontSize: '0.875rem',
      margin: '0 0 2rem 0',
    },
    skipFeatures: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1rem',
      marginBottom: '2rem',
    },
    featureItem: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem',
      fontSize: '0.875rem',
      padding: '0.75rem',
      borderRadius: '0.75rem',
      backgroundColor: 'rgba(51, 65, 85, 0.3)',
      transition: 'all 0.3s ease',
    },
    featureItemHover: {
      backgroundColor: 'rgba(59, 130, 246, 0.1)',
      transform: 'translateX(8px)',
    },
    featureIcon: {
      width: '18px',
      height: '18px',
      color: '#10b981',
    },
    featureIconWarning: {
      color: '#f59e0b',
    },
    featureText: {
      color: '#e2e8f0',
      flex: 1,
    },
    priceSection: {
      borderTop: '1px solid rgba(59, 130, 246, 0.2)',
      paddingTop: '2rem',
      marginTop: '2rem',
      position: 'relative',
    },
    priceGlow: {
      position: 'absolute',
      top: 0,
      left: '50%',
      transform: 'translateX(-50%)',
      width: '60%',
      height: '1px',
      background: 'linear-gradient(90deg, transparent, #3b82f6, transparent)',
      animation: 'price-glow 2s ease-in-out infinite',
    },
    priceRow: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontSize: '0.875rem',
      color: '#94a3b8',
      marginBottom: '0.75rem',
      transition: 'color 0.3s ease',
    },
    totalPrice: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#ffffff',
      marginTop: '1rem',
      paddingTop: '1rem',
      borderTop: '1px solid rgba(59, 130, 246, 0.2)',
      background: 'linear-gradient(135deg, #ffffff, #3b82f6)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text',
    },
    selectButton: {
      width: '100%',
      marginTop: '2rem',
      padding: '1.25rem',
      borderRadius: '1.25rem',
      fontWeight: '600',
      fontSize: '0.875rem',
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.75rem',
      position: 'relative',
      overflow: 'hidden',
    },
    selectButtonDefault: {
      background: 'linear-gradient(135deg, #374151, #4b5563)',
      color: '#d1d5db',
      border: '1px solid #4b5563',
    },
    selectButtonHover: {
      background: 'linear-gradient(135deg, #4b5563, #6b7280)',
      transform: 'translateY(-3px)',
      boxShadow: '0 12px 24px rgba(0, 0, 0, 0.3)',
    },
    selectButtonSelected: {
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
      color: 'white',
      boxShadow: '0 12px 24px rgba(59, 130, 246, 0.4)',
      animation: 'selected-glow 2s ease-in-out infinite alternate',
    },
    buttonShine: {
      position: 'absolute',
      top: 0,
      left: '-100%',
      width: '100%',
      height: '100%',
      background: 'linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent)',
      transition: 'left 0.6s ease',
    },
    bottomBar: {
      position: 'fixed',
      bottom: 0,
      left: 0,
      right: 0,
      background: 'linear-gradient(135deg, rgba(30, 41, 59, 0.95) 0%, rgba(51, 65, 85, 0.95) 100%)',
      backdropFilter: 'blur(20px)',
      borderTop: '1px solid rgba(59, 130, 246, 0.3)',
      padding: '2rem',
      boxShadow: '0 -20px 40px rgba(0, 0, 0, 0.3)',
      zIndex: 50,
      transform: selectedSkip ? 'translateY(0)' : 'translateY(100%)',
      transition: 'transform 0.5s cubic-bezier(0.4, 0, 0.2, 1)',
    },
    bottomBarContent: {
      maxWidth: '1200px',
      margin: '0 auto',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      gap: '2rem',
    },
    bottomBarInfo: {
      flex: 1,
    },
    bottomBarTitle: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#ffffff',
      margin: '0 0 0.5rem 0',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
    },
    bottomBarSubtitle: {
      color: '#94a3b8',
      fontSize: '0.875rem',
      margin: 0,
    },
    bottomBarActions: {
      display: 'flex',
      gap: '1rem',
      alignItems: 'center',
    },
    changeButton: {
      padding: '1rem 2rem',
      background: 'rgba(75, 85, 99, 0.5)',
      color: '#94a3b8',
      border: '1px solid rgba(75, 85, 99, 0.5)',
      borderRadius: '1rem',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      fontSize: '0.875rem',
      fontWeight: '500',
      backdropFilter: 'blur(10px)',
    },
    continueButton: {
      background: 'linear-gradient(135deg, #3b82f6, #8b5cf6)',
      color: 'white',
      padding: '1.25rem 2.5rem',
      borderRadius: '1rem',
      fontWeight: '600',
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      transition: 'all 0.3s ease',
      border: 'none',
      cursor: 'pointer',
      boxShadow: '0 8px 24px rgba(59, 130, 246, 0.4)',
      fontSize: '0.875rem',
      position: 'relative',
      overflow: 'hidden',
    },
  };

  const handleSkipSelect = (skip) => {
    setSelectedSkip(skip);
  };

  const handleClearSelection = () => {
    setSelectedSkip(null);
  };

  const handleContinue = () => {
    if (selectedSkip) {
      const totalPrice = calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat);
      alert(`Selected ${selectedSkip.size} Yard Skip - £${totalPrice}`);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={styles.container}>
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(180deg); }
        }
        
        @keyframes particle-float {
          0% { transform: translateY(100vh) rotate(0deg); opacity: 0; }
          10% { opacity: 0.6; }
          90% { opacity: 0.6; }
          100% { transform: translateY(-100px) rotate(360deg); opacity: 0; }
        }
        
        @keyframes pulse-glow {
          0%, 100% { box-shadow: 0 4px 12px rgba(59, 130, 246, 0.4); }
          50% { box-shadow: 0 4px 20px rgba(59, 130, 246, 0.8); }
        }
        
        @keyframes gradient-shift {
          0% { background-position: 0% 50%; }
          100% { background-position: 100% 50%; }
        }
        
        @keyframes pattern-move {
          0% { transform: translate(0, 0); }
          100% { transform: translate(30px, 30px); }
        }
        
        @keyframes badge-glow {
          0% { box-shadow: 0 8px 24px rgba(59, 130, 246, 0.4); }
          100% { box-shadow: 0 8px 32px rgba(59, 130, 246, 0.8); }
        }
        
        @keyframes success-pulse {
          0% { transform: scale(0.8); opacity: 0; }
          50% { transform: scale(1.1); }
          100% { transform: scale(1); opacity: 1; }
        }
        
        @keyframes price-glow {
          0%, 100% { opacity: 0.3; }
          50% { opacity: 1; }
        }
        
        @keyframes selected-glow {
          0% { box-shadow: 0 12px 24px rgba(59, 130, 246, 0.4); }
          100% { box-shadow: 0 12px 32px rgba(59, 130, 246, 0.8); }
        }
      `}</style>
      
      {/* Animated Background */}
      <div style={styles.backgroundPattern}></div>
      
      {/* Floating Particles */}
      {particleAnimation && (
        <div style={styles.floatingElements}>
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              style={{
                ...styles.particle,
                left: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 15}s`,
                animationDuration: `${15 + Math.random() * 10}s`,
              }}
            />
          ))}
        </div>
      )}

      {/* Header */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <div style={styles.stepIndicator}>
            <div style={{...styles.stepItem, ...styles.stepActive}}>
              <div style={styles.stepNumber}>3</div>
              <span>Select Skip</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <MapPin size={16} />
              <span>Permit Check</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <Calendar size={16} />
              <span>Choose Date</span>
            </div>
            <ChevronRight size={16} />
            <div style={styles.stepItem}>
              <Shield size={16} />
              <span>Payment</span>
            </div>
          </div>
          
          <h1 style={styles.headerTitle}>Choose Your Skip Size</h1>
          <p style={styles.headerSubtitle}>
            Select the skip size that best suits your needs
          </p>
        </div>
      </div>

      {/* Main Content */}
      <div style={styles.main}>
        <div style={styles.skipGrid}>
          {skips.map((skip, index) => {
            const isSelected = selectedSkip?.id === skip.id;
            const isHovered = hoveredSkip === skip.id;
            const totalPrice = calculateTotalPrice(skip.price_before_vat, skip.vat);
            const vatAmount = (skip.price_before_vat * skip.vat / 100).toFixed(0);

            const cardStyle = {
              ...styles.skipCard,
              transitionDelay: `${index * 0.1}s`,
              ...(isSelected ? styles.skipCardSelected : {}),
              ...(isHovered && !isSelected ? styles.skipCardHover : {}),
            };

            const buttonStyle = {
              ...styles.selectButton,
              ...(isSelected ? styles.selectButtonSelected : styles.selectButtonDefault),
              ...(isHovered && !isSelected ? styles.selectButtonHover : {}),
            };

            const imageStyle = {
              ...styles.skipImage,
              ...(isHovered ? styles.skipImageHover : {}),
            };

            return (
              <div
                key={skip.id}
                style={cardStyle}
                onClick={() => handleSkipSelect(skip)}
                onMouseEnter={() => setHoveredSkip(skip.id)}
                onMouseLeave={() => setHoveredSkip(null)}
              >
                {/* Skip Image */}
                <div style={styles.skipImageContainer}>
                  <div style={styles.skipImagePattern}></div>
                  <div style={imageStyle}>
                    <div style={styles.skipImageSide}></div>
                    <div style={styles.skipImageBottom}></div>
                  </div>
                  
                  <div style={styles.sizeBadge}>
                    <Sparkles size={14} style={{ marginRight: '0.5rem' }} />
                    {skip.size} Yards
                  </div>
                  
                  {isSelected && (
                    <div style={styles.selectedBadge}>
                      <Check size={18} />
                    </div>
                  )}
                </div>

                {/* Card Content */}
                <div style={styles.skipCardContent}>
                  <h3 style={styles.skipTitle}>{skip.size} Yard Skip</h3>
                  <p style={styles.skipSubtitle}>{skip.hire_period_days} day hire period</p>

                  {/* Features */}
                  <div style={styles.skipFeatures}>
                    <div style={{
                      ...styles.featureItem,
                      ...(isHovered ? styles.featureItemHover : {})
                    }}>
                      <Clock style={styles.featureIcon} size={18} />
                      <span style={styles.featureText}>
                        {skip.hire_period_days} days included
                      </span>
                    </div>
                    
                    <div style={{
                      ...styles.featureItem,
                      ...(isHovered ? styles.featureItemHover : {})
                    }}>
                      <Truck 
                        style={skip.allowed_on_road ? styles.featureIcon : styles.featureIconWarning} 
                        size={18} 
                      />
                      <span style={styles.featureText}>
                        {skip.allowed_on_road ? 'Road placement allowed' : 'Private land only'}
                      </span>
                    </div>
                    
                    <div style={{
                      ...styles.featureItem,
                      ...(isHovered ? styles.featureItemHover : {})
                    }}>
                      <Weight 
                        style={skip.allows_heavy_waste ? styles.featureIcon : styles.featureIconWarning} 
                        size={18} 
                      />
                      <span style={styles.featureText}>
                        {skip.allows_heavy_waste ? 'Heavy waste accepted' : 'Light waste only'}
                      </span>
                    </div>
                  </div>

                  {/* Pricing */}
                  <div style={styles.priceSection}>
                    <div style={styles.priceGlow}></div>
                    <div style={styles.priceRow}>
                      <span>Base price</span>
                      <span>£{skip.price_before_vat}</span>
                    </div>
                    <div style={styles.priceRow}>
                      <span>VAT ({skip.vat}%)</span>
                      <span>£{vatAmount}</span>
                    </div>
                    <div style={styles.totalPrice}>
                      <span>Total</span>
                      <span>£{totalPrice}</span>
                    </div>
                  </div>

                  {/* Select Button */}
                  <button style={buttonStyle}>
                    <div style={styles.buttonShine}></div>
                    {isSelected ? (
                      <>
                        <Check size={18} />
                        Selected
                      </>
                    ) : (
                      <>
                        <Zap size={18} />
                        Select This Skip
                      </>
                    )}
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      {/* Bottom Selection Bar */}
      <div style={styles.bottomBar}>
        <div style={styles.bottomBarContent}>
          <div style={styles.bottomBarInfo}>
            <h3 style={styles.bottomBarTitle}>
              <Sparkles size={20} />
              Selected: {selectedSkip?.size} Yard Skip
            </h3>
            <p style={styles.bottomBarSubtitle}>
              £{selectedSkip ? calculateTotalPrice(selectedSkip.price_before_vat, selectedSkip.vat) : 0} including VAT
            </p>
          </div>
          
          <div style={styles.bottomBarActions}>
            <button
              onClick={handleClearSelection}
              style={styles.changeButton}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = 'rgba(75, 85, 99, 0.8)';
                e.target.style.color = '#ffffff';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = 'rgba(75, 85, 99, 0.5)';
                e.target.style.color = '#94a3b8';
              }}
            >
              Change Selection
            </button>
            <button
              onClick={handleContinue}
              style={styles.continueButton}
              onMouseEnter={(e) => {
                e.target.style.transform = 'translateY(-2px) scale(1.05)';
                e.target.style.boxShadow = '0 12px 32px rgba(59, 130, 246, 0.6)';
              }}
              onMouseLeave={(e) => {
                e.target.style.transform = 'translateY(0) scale(1)';
                e.target.style.boxShadow = '0 8px 24px rgba(59, 130, 246, 0.4)';
              }}
            >
              <span>Continue</span>
              <ChevronRight size={18} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SkipSelector;
