import React from 'react';
import { Calendar, Truck, Weight, Check } from 'lucide-react';
import SkipStatusIndicator from './SkipStatusIndicator';
import PriceBreakdown from './PriceBreakdown';
import { calculateTotalPrice } from '../../utils/priceCalculator';

const SkipCard = ({ skip, isSelected, onSelect }) => {
  const totalPrice = calculateTotalPrice(skip.price_before_vat, skip.vat);

  return (
    <div
      className={`bg-white rounded-2xl shadow-lg cursor-pointer transition-all duration-300 relative border-2 ${
        isSelected 
          ? 'border-blue-500 shadow-blue-200 shadow-2xl transform -translate-y-1' 
          : 'border-transparent hover:shadow-xl'
      }`}
      onClick={() => onSelect(skip)}
    >
      {isSelected && (
        <div className="absolute -top-2 -right-2 bg-blue-500 rounded-full w-8 h-8 flex items-center justify-center z-10">
          <Check size={16} color="white" />
        </div>
      )}

      <div className={`p-6 pb-4 rounded-t-2xl ${isSelected ? 'bg-blue-50' : 'bg-gray-50'}`}>
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-2xl font-bold text-gray-900">{skip.size}</h3>
          <span className="text-sm font-medium text-gray-600">YARD</span>
        </div>
        <div className="text-3xl font-bold text-blue-600">
          Â£{totalPrice}
          <span className="text-sm font-normal text-gray-600 ml-1">inc VAT</span>
        </div>
      </div>

      <div className="p-6 pt-4">
        <div className="mb-4">
          <div className="flex items-center text-sm text-gray-600 mb-3">
            <Calendar size={16} className="mr-2 text-gray-400" />
            {skip.hire_period_days} day hire period
          </div>
          
          <SkipStatusIndicator
            icon={Truck}
            isPositive={skip.allowed_on_road}
            label={skip.allowed_on_road ? 'Road placement allowed' : 'Private land only'}
          />
          
          <SkipStatusIndicator
            icon={Weight}
            isPositive={skip.allows_heavy_waste}
            label={skip.allows_heavy_waste ? 'Heavy waste accepted' : 'Light waste only'}
          />
        </div>

        <PriceBreakdown skip={skip} />

        <button
          className={`w-full mt-4 py-3 px-4 rounded-xl font-semibold transition-all duration-200 ${
            isSelected
              ? 'bg-blue-500 text-white shadow-lg'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
          }`}
        >
          {isSelected ? 'Selected' : 'Select This Skip'}
        </button>
      </div>
    </div>
  );
};
export default SkipCard;
